def extended_gcd(a, b):
    if a == 0:
        return b, 0, 1
    else:
        gcd, x, y = extended_gcd(b % a, a)
        return gcd, y - (b // a) * x, x
def solve(e1, e2, n, c1, c2):
    d, x, y = extended_gcd(e1, e2)    
    m = (pow(c1, x, n) * pow(c2, y, n)) % n
    return m
